import { motion, useMotionValue, useScroll, useTransform } from "framer-motion";
import React from "react";

function App() {

	const x = useMotionValue(0)
	const color = useTransform(
		x,
		[-100, 0, 100],
		['#f00', '#0f0', '#00f']
	);
	const { scrollXProgress } = useScroll();
	console.log(scrollXProgress);

	return (
		<motion.div
			style={{ opacity: scrollXProgress, padding: '5rem', background: "red", width: '100px', x, color, display: 'flex', position: 'relative', left: '500px' }}
			drag='x'

		> AA</ motion.div >
	);
}
export default App;
