import React, { useState } from "react";
import Input from "../../UI/Input";
import { initialStateType } from "../store/TodoSlice";
import Span from "../../UI/Span";
import { BaseElemetPropsType } from "../../UI/BaseElemetPropsType";
import Button from "../../UI/Button";

function Item(props: initialStateType): JSX.Element {
	let { title, complete, content } = props;
	let id: string = props.id.toString();

	const [isEdit, setIsEdit] = useState(false);
	const [contentText, setContentTextt] = useState(content);

	let eleDefaultProps: BaseElemetPropsType = { id, name: title, title };

	const eleInput = <Input {...eleDefaultProps} type="text" value={contentText} onChange={(e) => setContentTextt(e.currentTarget.value)} />;
	const eleCheckBox = <Input {...eleDefaultProps} type="checkBox" value={contentText} onChange={(e) => setContentTextt(e.currentTarget.value)} />;
	const eleSpan = <Span {...eleDefaultProps} />;
	const eleSaveButton = <Button {...eleDefaultProps} title="Save" onClick={() => setIsEdit(false)} />;
	const eleEditButton = <Button {...eleDefaultProps} title="Edit" onClick={() => setIsEdit(true)} />;
	const eleDeleteButton = <Button {...eleDefaultProps} title="Delete" />;

	return (
		<div>
			<form action="" onSubmit={(e) => e.preventDefault()}>
				{isEdit ? eleInput : eleSpan}
				{isEdit ? eleSaveButton : eleEditButton}
				{eleDeleteButton}
			</form>
		</div>
	);
}

export default Item;
