import { motion, useMotionValue, useTransform } from "framer-motion";
import React from "react";

function App() {

	const x = useMotionValue(0)
	const opacity = useTransform(x, [-100, 0, 100, 200], [0, 1, 0, 1])

	console.log("x : ", x);
	console.log("opacity : ", opacity);

	return (
		<motion.div drag="x" style={{ opacity, x }} > AA</ motion.div >
	);
}
export default App;
