import { AnimatePresence, motion, useMotionValue, useScroll, useTransform } from "framer-motion";
import React from "react";
import { Route, Routes } from "react-router-dom";

function App() {

	const x = useMotionValue(0)
	const color = useTransform(
		x,
		[-100, 0, 100],
		['#f00', '#0f0', '#00f']
	);
	const { scrollXProgress, scrollX } = useScroll();

	return (
		<Routes>
			<Route path="home" element={<motion.div
				style={{ opacity: scrollXProgress, padding: '5rem', background: "red", width: '100px', x, color, display: 'flex', position: 'relative', left: '500px' }}
				drag='x'
				exit={{ scale: 10 }}
			> AA</ motion.div >}>

			</Route>
			<Route>
				<h2>
					Hello
				</h2>
			</Route>
		</Routes>
	);
}
export default App;
