import React, { useState } from "react";
import Input from "../../UI/Input";
import { TodoActions, initialStateType, todoType } from "../store/todoReducer";
import Span from "../../UI/Span";
import { BaseElemetPropsType } from "../../UI/BaseElemetPropsType";
import Button from "../../UI/Button";
import { useDispatch } from "react-redux";

function Item({ id, title, complete, content }: todoType): JSX.Element {
	const [isEdit, setIsEdit] = useState(false);
	const [contentText, setContentTextt] = useState(content);
	const dispatch = useDispatch();

	let eleDefaultProps: BaseElemetPropsType = { id: id.toString(), name: title, title };

	// imput
	const eleCheckBox = (
		<Input
			{...eleDefaultProps}
			type="checkBox"
			checked={complete}
			onChange={() => {
				return dispatch(TodoActions.Complete({ id }));
			}}
		/>
	);
	//lable
	const eleTitle = <Span {...eleDefaultProps} />;

	return (
		<div>
			{eleCheckBox}
			{eleTitle}
		</div>
	);
}

export default Item;
