import { Draft, createSlice } from "@reduxjs/toolkit";
import type { PayloadAction } from "@reduxjs/toolkit";

export interface initialStateType {
	list: [{ id: number; title: string; content: string; complete: boolean }];
}

const initialState: initialStateType = { list: [{ id: 1, title: "new title", complete: false, content: "new content" }] };

const TodoSlice = createSlice({
	name: "Todo",
	initialState,
	reducers: {
		Add(state, actions: PayloadAction<initialStateType>) {
			state.list.unshift(actions.payload);
		},
		Complete(state, action: PayloadAction<{ id: number }>) {
			let item = getItem(action.payload.id, state.list);
			if (item) {
				item.complete = !item.complete;
			}
		},
		Save(state: Draft<initialStateType>, { payload }: PayloadAction<{ id: number; content: string }>): void {
			let item = getItem(payload.id, state.list);
			if (item) item.content = payload.content;
		},
		Delete(state, { payload }: PayloadAction<{ id: number }>) {
			state.list.filter((x) => x.id !== payload.id);
		},
	},
});

export const TodoActions = TodoSlice.actions;

function getItem(id: number, list: Partial<initialStateType>["list"]) {
	let item = list.find((x) => x.id === id);
	if (item) return item;
	return undefined;
}

export default TodoSlice.reducer;
