import React, { useState } from "react";
import Input from "../../UI/Input";
import { TodoActions, initialStateType, todoType } from "../store/todoReducer";
import Span from "../../UI/Span";
import { BaseElemetPropsType } from "../../UI/BaseElemetPropsType";
import Button from "../../UI/Button";
import { useDispatch } from "react-redux";
import Form from "./Form";
import Lable from "../../UI/Lable";

function Item(props: todoType): JSX.Element {
	let { id, title, complete } = props;
	const dispatch = useDispatch();
	let eleDefaultProps: BaseElemetPropsType = { id: props.id.toString(), title };

	// imput
	const eleCheckBox = (
		<Input
			name={`${title}-${id}`}
			{...eleDefaultProps}
			type="checkBox"
			checked={complete}
			onChange={() => {
				return dispatch(TodoActions.Complete({ id }));
			}}
		/>
	);
	//lable
	const eleTitle = <Lable {...eleDefaultProps} form={`${title}-${id}`} htmlFor="" />;

	return (
		<div>
			{eleCheckBox}
			{eleTitle}
			<Form />
		</div>
	);
}

export default Item;
