import { motion, useMotionValue, useTransform } from "framer-motion";
import React from "react";

function App() {

	const x = useMotionValue(0)
	const color = useTransform(x, [800, 1000, 1200], ['orange', 'green', 'yellow'])


	return (
		<motion.div
			style={{ padding: '5rem', background: "red", display: 'flex' }}
			initial={{ opacity: 0, scale: 0.45 }}
			animate={{ opacity: 1, scale: 2 }}
		> AA</ motion.div >
	);
}
export default App;
