import React, { useState } from "react";
import Input from "../../UI/Input";
import { TodoActions, initialStateType } from "../store/TodoSlice";
import Span from "../../UI/Span";
import { BaseElemetPropsType } from "../../UI/BaseElemetPropsType";
import Button from "../../UI/Button";
import { useDispatch } from "react-redux";
import { Dispatch } from "redux";
import { TodoDispatchType } from "../store/TodoRedux";

function Item({ id, title, complete, content }: initialStateType): JSX.Element {
	const [isEdit, setIsEdit] = useState(false);
	const [contentText, setContentTextt] = useState(content);
	const dispatch = useDispatch();

	let eleDefaultProps: BaseElemetPropsType = { id: id.toString(), name: title, title };

	const eleInput = <Input {...eleDefaultProps} type="text" value={contentText} onChange={(e) => setContentTextt(e.currentTarget.value)} />;
	const eleCheckBox = (
		<Input
			{...eleDefaultProps}
			type="checkBox"
			checked={complete}
			onChange={() => {
				return dispatch(TodoActions.Complete({ id }));
			}}
		/>
	);
	const eleTitle = <Span {...eleDefaultProps} />;
	const eleContent = <Span {...eleDefaultProps} title={content} />;
	// buttons
	const eleSaveButton = (
		<Button
			{...eleDefaultProps}
			title="Save"
			onClick={() => {
				setIsEdit(false);
				dispatch(TodoActions.Save({ id, content: content }));
			}}
		/>
	);
	const eleEditButton = <Button {...eleDefaultProps} title="Edit" onClick={() => setIsEdit(true)} />;
	const eleDeleteButton = <Button {...eleDefaultProps} title="Delete" />;

	return (
		<div>
			{eleCheckBox}
			{eleTitle}
			<hr />
			{isEdit ? eleInput : eleContent}
			<hr />
			{isEdit ? eleSaveButton : eleEditButton}
			{eleDeleteButton}
		</div>
	);
}

export default Item;
