import { createSlice } from "@reduxjs/toolkit";
import type { PayloadAction } from "@reduxjs/toolkit";

export type initialStateType = { id: number; title: string; content: string; complete: boolean };

const TodoSlice = createSlice({
	name: "Todo",
	initialState: { list: [{ id: 1, title: "new work", complete: false, content: "get up" }] as initialStateType[] },
	reducers: {
		Add(state, actions: PayloadAction<initialStateType>) {
			state.list.unshift(actions.payload);
		},
		Complete(state, action: PayloadAction<{ id: number }>) {
			let item = state.list[findIndex(action.payload.id, state.list)];
			if (item) {
				item.complete = !item.complete;
			}
		},
	},
});

export const TodoActions = TodoSlice.actions;

function findIndex(id: number, list: initialStateType[]): number {
	let item = list.find((x) => x.id === id);
	if (item) return list.indexOf(item);
	return -1;
}

export default TodoSlice.reducer;
