import { motion, useMotionValue, useTransform } from "framer-motion";
import React from "react";

function App() {

	const x = useMotionValue(0)
	const color = useTransform(x, [800, 1000, 1200], ['orange', 'green', 'yellow'])


	return (
		<motion.div
			style={{ padding: '5rem', background: "red", width: '100px' }}
			whileHover={{ scale: 1.5 }}
			whileTap={{ scale: .9 }}
			transition={{ duration: 1, ease: 'linear' }}
		> AA</ motion.div >
	);
}
export default App;
