import { motion, useMotionValue, useTransform } from "framer-motion";
import React from "react";

function App() {

	const x = useMotionValue(0)
	const opacity = useTransform(x, [-100, 0, 100], [0, 0.2, 1])

	return (
		<motion.div drag="x" style={{ x, opacity }} >AA {x.hasAnimated}</motion.div>
	);
}
export default App;
