import { motion, useMotionValue, useTransform } from "framer-motion";
import React from "react";

function App() {

	const x = useMotionValue(200)
	const color = useTransform(x, [100, 200, 300], ['red', 'green', 'yellow'])


	return (
		<motion.div drag="x" style={{ color, x }} > AA</ motion.div >
	);
}
export default App;
