import { motion, useMotionValue, useTransform } from "framer-motion";
import React from "react";

function App() {

	const x = useMotionValue(0)
	const color = useTransform(
		x,
		[-100, 0, 100],
		['#f00', '#0f0', '#00f']
	);


	return (
		<motion.div
			style={{ padding: '5rem', background: "red", width: '100px', x, color, display: 'flex', position: 'relative', left: '500px' }}
			drag='x'
			viewport={{ once: false, amount: 'some' }}
		> AA</ motion.div >
	);
}
export default App;
