import React, { useState } from "react";
import Input from "../../UI/Input";
import { TodoActions, initialStateType, todoType } from "../store/todoReducer";
import Span from "../../UI/Span";
import { BaseElemetPropsType } from "../../UI/BaseElemetPropsType";
import Button from "../../UI/Button";
import Textarea from "../../UI/Textarea";
import { AnyAction, Dispatch } from "redux";
function Form({ id, title, content, dispatch }: todoType & { dispatch: Dispatch<AnyAction> }): JSX.Element {
	const [isEdit, setIsEdit] = useState(false);
	const [contentText, setContentText] = useState(content);

	let eleDefaultProps: BaseElemetPropsType = { id: id.toString(), name: title, title };

	// imput
	const eleInput = <Textarea {...eleDefaultProps} value={contentText} onChange={(e) => setContentText(e.currentTarget.value)} />;

	//lable
	const eleContent = <Span {...eleDefaultProps} title={contentText} />;
	// buttons
	const eleSaveButton = (
		<Button
			{...eleDefaultProps}
			title="Save"
			onClick={() => {
				setIsEdit(false);
				dispatch(TodoActions.Edit({ id, content: contentText }));
			}}
		/>
	);
	const eleEditButton = <Button {...eleDefaultProps} title="Edit" onClick={() => setIsEdit(true)} />;
	const eleDeleteButton = <Button {...eleDefaultProps} title="Delete" onClick={() => dispatch(TodoActions.Delete({ id }))} />;

	return (
		<form
			action=""
			id="form-1"
			onSubmit={(e) => {
				e.preventDefault();
			}}>
			{isEdit ? eleInput : eleContent}
			{isEdit ? eleSaveButton : eleEditButton}
			{eleDeleteButton}
		</form>
	);
}

export default Form;
