import { AnimatePresence, motion, useMotionValue, useScroll, useTransform } from "framer-motion";
import React from "react";

function App() {

	const x = useMotionValue(0)
	const color = useTransform(
		x,
		[-100, 0, 100],
		['#1f19cc', '#0f0', '#00f']
	);
	const { scrollXProgress } = useScroll();
	console.log(scrollXProgress);

	return (
		<AnimatePresence>
			<motion.div
				style={{ padding: '5rem', background: "red", width: '100px', x, color, display: 'flex', position: 'relative', left: '500px' }}
				drag='x'
				exit={{ scale: 10 }}
			> AA</ motion.div >
		</AnimatePresence>
	);
}
export default App;
