import Input from "../../UI/Input";
import { TodoActions, todoType } from "../store/todoReducer";
import { BaseElemetPropsType } from "../../UI/BaseElemetPropsType";
import { useDispatch } from "react-redux";
import Form from "./Form";
import Lable from "../../UI/Lable";
import "./style.scss";

function Item(props: todoType): JSX.Element {
	let { id, title, complete } = props;
	const dispatch = useDispatch();
	let eleDefaultProps: BaseElemetPropsType = { id: props.id.toString(), title };

	// imput
	const eleCheckBox = (
		<Input
			{...eleDefaultProps}
			name={`c-${title}-${id}`}
			type="checkBox"
			checked={complete}
			className="checkbox"
			onChange={() => {
				return dispatch(TodoActions.Complete({ id }));
			}}
		/>
	);
	//lable
	const eleTitle = <Lable {...eleDefaultProps} form={`${title}-${id}`} htmlFor="" className="lable" />;

	return (
		<div>
			{eleCheckBox}
			{eleTitle}
			<Form {...props} dispatch={dispatch} />
		</div>
	);
}

export default Item;
